const GRAPHCMS_API = 'http://localhost:8000/graphql'

function createClient (headers) {
  const networkInterface = createNetworkInterface({
    uri: GRAPHCMS_API,
    ops: {
      credentials: 'same-origin'
    }
  })
  return new ApolloClient({ networkInterface })
}
