import React, { Component } from 'react'
import Header from './Header'

export default class Layout extends Component {
  render() {
    const layoutStyle = {
      margin: 20,
      padding: 20.
    }
    return (
      <div style={layoutStyle}>
        <Header />
        {this.props.children}
      </div>
    )
  }
}
